import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'labufgII';
}
<div class="container">
  <h1>Calculadora de Salario</h1>

  <label for="salary">Ingresar Salario:</label>
  <input [(ngModel)]="salary" id="salary" type="number">

  <button (click)="calculate()">Calcular</button>

  <div *ngIf="showResults">
    <p><strong>Salario Base:</strong> {{ salary | currency:'USD':'symbol':'1.2-2' }}</p>
    <p><strong>Descuento de renta:</strong> {{ renta | currency:'USD':'symbol':'1.2-2' }}</p>
    <p><strong>Descuento AFP:</strong> {{ afp | currency:'USD':'symbol':'1.2-2' }}</p>
    <p><strong>Descuento ISSS:</strong> {{ isss | currency:'USD':'symbol':'1.2-2' }}</p>
    <p><strong>Salario liquido a cancelar:</strong> {{ netSalary | currency:'USD':'symbol':'1.2-2' }}</p>
  </div>
</div>
