import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  salary: number = 0;
  renta: number = 0;
  afp: number = 0;
  isss: number = 0;
  netSalary: number = 0;
  showResults: boolean = false;

  calculate() {
    this.calculateRenta();
    this.calculateAFP();
    this.calculateISSS();
    this.netSalary = this.salary - this.renta - this.afp - this.isss;
    this.showResults = true;
  }

  calculateRenta() {

    this.renta = this.salary * 0.10;  
  }

  calculateAFP() {
    const AFP_PERCENTAGE = 0.0725;  
    this.afp = this.salary * AFP_PERCENTAGE;
  }

  calculateISSS() {
    const ISSS_PERCENTAGE = 0.03;  
    this.isss = this.salary * ISSS_PERCENTAGE;
  }
}
<div class="container">
  <h1>Calculadora de Salario</h1>

  <label for="salary">Ingresar Salario:</label>
  <input  id="salary" type="number">

  <button (click)="calculate()">Calcular</button>

  <div *ngIf="showResults">
    <p><strong>Salario Base:</strong></p>
    <p><strong>Descuento de renta:</strong> </p>
    <p><strong>Descuento AFP:</strong></p>
    <p><strong>Descuento ISSS:</strong> </p>
    <p><strong>Salario liquido a cancelar:</strong></p>
  </div>
</div>
